import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from "@material-ui/core/Grid";
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Avatar from '@material-ui/core/Avatar';

const styles = theme => ({
    root: {width: 'auto', display: 'block',padding: '50px 0'},
    paper: {width: 600, margin: 'auto', padding: '40px 30px',},
    input: {width: 400},
    button: {margin: 20, width: 150,},
    rightIcon: {marginLeft: theme.spacing.unit,},
    divider: {margin: '0 0 20px 0'},
    bigAvatar: {margin: 10, width: 100, height: 100, cursor: 'pointer'},
    fileInput: {display: 'none',},
});

const obj2params = obj =>  {
    let result = '';
    let item;
    for(item in obj){
        result += '&' + item + '=' +encodeURIComponent(obj[item]);
    }
    if(result) {
        result = result.slice(1);
    }
    return result;
};

class Complete extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            err: {
                nickname: false,
                password: false
            },
            validate: {
                nickname: false,
                password: false
            },
            data: {
                token: "",
                email: "",
                nickname: "",
                password: "",
                avatar: "go.jpg"
            }
        }
    }

    componentDidMount() {
        let payload = this.props.match.params.token.split(".")[1];
        let tokenObj = JSON.parse(atob(payload));
        let now = (new Date()).getTime();
        if (!tokenObj.email) {
            alert("token invalid");
            this.props.history.push("/register");
            return;
        }
        if (now > tokenObj.exp * 1000) {
            alert("token expired, please register again");
            this.props.history.push("/register");
            return;
        }
        this.setState({
            data:{
                token: this.props.match.params.token,
                email: tokenObj.email
            }
        });
    }

    handleName(e) {
        this.setState({
            data: {
                nickname: e.target.value
            }
        });
        if(this.state.data.nickname === "") {
            this.setState({
                err: {
                    nickname: false,
                },
                validate: {
                    nickname: false,
                }
            });
        } else if (this.state.data.nickname.length < 4) {
            this.setState({
                err: {
                    nickname: true,
                },
                validate: {
                    nickname: false,
                }
            });
        } else {
            this.setState({
                err: {
                    nickname: false,
                },
                validate: {
                    nickname: true,
                }
            });
        }
    }

    handlePassword(e) {
        this.setState({
            data: {
                password: e.target.value
            }
        });
        if(this.state.data.nickname === "") {
            this.setState({
                err: {
                    password: false,
                },
                validate: {
                    password: false,
                }
            });
        } else if (this.state.data.password.length < 4) {
            this.setState({
                err: {
                    password: true,
                },
                validate: {
                    password: false,
                }
            });
        } else {
            this.setState({
                err: {
                    password: false,
                },
                validate: {
                    password: true,
                }
            });
        }
    }
    handleSubmit() {
        if(this.state.validate.nickname && this.state.validate.password) {
            fetch("http://localhost:8080/register", {
                method: "POST",
                mode: "cors",
                body: obj2params({
                    token: this.state.data.token,
                    nickname: this.state.data.nickname,
                    password: this.state.data.password
                }),

            });
        }
    }
    render() {
        const { classes } = this.props;
        return (
            <div className={classes.root}>
                <CssBaseline/>
                <Paper className={classes.paper} elevation={1}>
                    <Typography component="h1" variant="h5" color="textPrimary" gutterBottom>
                        Complete
                    </Typography>
                    <Divider variant="middle" className={classes.divider} />
                    <Grid container justify="center">
                        <input
                            accept="image/*"
                            className={classes.fileInput}
                            id="contained-button-file"
                            multiple
                            type="file"
                        />
                        <label htmlFor="contained-button-file">
                            <Avatar alt="Remy Sharp" src={this.state.data.avatar} className={classes.bigAvatar} />
                        </label>
                    </Grid>
                    <Grid container justify="center">
                        <TextField id="standard-search" disabled label="Email" type="text" value={this.state.data.email} className={classes.input} margin="normal"/>
                        <TextField id="standard-search" onChange={e => this.handleName(e)} label="Nickname" type="text" className={classes.input} margin="normal"/>
                        <TextField id="standard-search" onChange={e => this.handlePassword(e)} label="Password" type="password" className={classes.input} margin="normal"/>
                        <Button variant="contained" onClick={() => this.handleSubmit()} color="primary" className={classes.button} size="large">
                            COMPLETE
                        </Button>
                    </Grid>
                </Paper>
            </div>
        );
    }
}

Complete.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Complete);
