import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Profile from "./Profile"

const storage = window.localStorage;
storage.token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoxLCJlbWFpbCI6InN0ZXZ6aGFuZzAxQGdtYWlsLmNvbSIsIm1vYmlsZSI6IjEzOTE5MTU0OTI1Iiwibmlja19uYW1lIjoiU3RldkUuWiIsImltYWdlX3VybCI6IiIsImV4cCI6MTU1NzU2MDAxOCwiaWF0IjoxNTU3NTU5NzE4fQ.aNw5f-afjmhb-E_9m3m73Bc8MNuPteeDtRmOdrM7hHk";
class User extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            userInfo: {}
        };
    }
    componentDidMount() {
        fetch('http://localhost:8080/user/profile/', {
            method: "GET",
            mode: "cors",
            cache: 'default',
            headers: {
                "x-originals-token": storage.token
            }
        }).then(rst => {
           let data = rst.json()
            if (data.code === 200) {
                this.setState({
                    userInfo: data.result
                });
                this.props.history.push("/user/:" + this.state.userInfo.user_id)
            }
        }).catch(err => {
            console.log(err)
        })
    }

    render() {
        return (
            <Router>
                <div>
                    <Route path="/user/:{ this.state.userInfo.user_id}" userinfo={this.state.userInfo} component={Profile}/>
                </div>
            </Router>
        );
    }
}

export default User;
