import React from 'react';
import Typography from "@material-ui/core/Typography";

const storage = window.localStorage;
storage.token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoxLCJlbWFpbCI6IN0ZXZ6aGFuZzAxQGdtYWlsLmNvbSIsIm1vYmlsZSI6IjEzOTE5MTU0OTI1Iiwibmlja19uYW1lIjoiU3RldkUuWiIsImltYWdlX3VybCI6IiIsImV4cCI6MTU1NzU2MzgyNSwiaWF0IjoxNTU3NTYyNjI1fQ.jFBErLDQgxCp7s8ijkCTmo2uEI1TSB0jpLmlm_oAWcg";
class User extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            email: ""
        };
    }
    componentDidMount() {
        fetch('http://localhost:8080/user/profile/', {
            method: "GET",
            mode: "cors",
            cache: 'default',
            headers: {
                "x-originals-token": storage.token
            }
        }).then(res => {
            return res.json()
        }).then(data => {
            if (data.code === 200) {
                this.setState({
                    email: data.result.Email
                })
            } else {
                this.props.history.push("/login")
            }
        })
    }

    render() {
        return (
                <div>
                    <Typography component="h1" variant="h5">
                        {this.state.email}
                    </Typography>
                </div>
        );
    }
}

export default User;
