import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Profile from "./Profile"

const storage = window.localStorage;
storage.token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoxLCJlbWFpbCI6InN0ZXZ6aGFuZzAxQGdtYWlsLmNvbSIsIm1vYmlsZSI6IjEzOTE5MTU0OTI1Iiwibmlja19uYW1lIjoiU3RldkUuWiIsImltYWdlX3VybCI6IiIsImV4cCI6MTU1NzU1OTA0MCwiaWF0IjoxNTU3NTU4NzQwfQ.o87Ph29M11IRUyp-LC5C_KAkd4oAHd_JqyyULmpp5K0";
class User extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            userInfo: {}
        };
    }
    componentDidMount() {
        fetch('http://localhost:8080/user/profile/', {
            headers: {
                "x-originals-token": storage.token
            }
        }).then(rst => {
            user = rst.json()

        }).catch(err => {
            console.log(err)
        })
    }

    render() {
        return (
            <div>
                <Router>
                    <div>
                        <Route path="/user/:userId" component={Profile}/>
                    </div>
                </Router>
            </div>
        );
    }
}

export default User;
