import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from "@material-ui/core/Grid";
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import FormControl from '@material-ui/core/FormControl';
import FormHelperText from '@material-ui/core/FormHelperText';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';




const styles = (theme) => ({
    container: {
        marginTop: 30

    },
    paper: {
        maxWidth: 500,
        margin: 'auto',
        padding: 30,
    },
    divider: {
        margin: 0
    },
    grid: {
        margin: '20px 0',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
    },
    formControl: {
        width: '60%',
        [theme.breakpoints.down('sm')]: {
            width: '80%',
        },
    },
    button: {
        height: 40,
        marginTop: 20
    }
});

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            disabled: false,
            email: "",
            emailErr: false,
            emailValid: false
        };
    }
    handleEmail(e) {
        this.setState({
            email: e.target.value,
        });
        let reg = new RegExp("^[a-z0-9]+([._\\-]*[a-z0-9])*@([a-z0-9]+[-a-z0-9]*[a-z0-9]+.){1,63}[a-z0-9]+$");
        if(this.state.email !== "" && !reg.test(this.state.email)){
            this.setState({
                emailErr: true,
                emailValidate: false
            });
        } else if (this.state.email === "") {
            this.setState({
                emailErr: false,
                emailValidate: false
            })
        } else {
            this.setState({
                emailErr: false,
                emailValidate: true
            })
        }
    }
    handleSubmit() {
        if(this.state.emailValidate){
            this.setState({
                disabled: true
            });
            fetch("http://www.koogo.net:8080/user/auth/register", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: this.state.email
                })
            }).then(rsp => {
                return rsp.json();
            }).then(data => {
                if (data.code === 200) {
                    alert("register success, an validation email has been send to your mail box")
                    this.props.history.push("/login");
                } else {
                    this.setState({
                        disabled: false,
                        emailErr: true
                    });
                    alert(data.message);
                }
            }).catch(err => {
                this.setState({
                    disabled: false
                });
                alert(err);
            });
        } else {
            this.setState({
                emailErr: true
            });
        }
    }
    render() {
        const { classes } = this.props;
        return (
            <div className={classes.container}>
                <CssBaseline/>
                <Paper className={classes.paper}>
                    <Typography component="h3" variant="h6" color="textPrimary" align="right" gutterBottom>
                        Register |  <a href="/login">Login</a>
                    </Typography>
                    <Divider variant="middle" className={classes.divider} />
                    <Grid container justify="center" alignItems="column" className={classes.grid} aria-disabled={this.state.disabled}>
                        <FormControl className={classes.formControl} error={this.state.emailErr}>
                            <InputLabel htmlFor="register-email">Email</InputLabel>
                            <Input
                                id="register-email"
                                value={this.state.name}
                                onChange={this.handleChange}
                                aria-describedby="register-email-error"
                            />

                            <FormHelperText id="register-email-error">Error</FormHelperText>
                        </FormControl>
                        <Button onClick={() => this.handleSubmit()} variant="outlined" color="primary" className={classes.button} size="large">
                            REGISTER
                        </Button>
                    </Grid>
                </Paper>
            </div>
        );
    }
}

Register.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Register);
