import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class Profile extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            userInfo: props.userInfo
        };
    }
    componentDidMount() {
        fetch('http://localhost:8080/user/profile/', {
            method: "GET",
            mode: "cors",
            cache: 'default',
            headers: {
                "x-originals-token": storage.token
            }
        }).then(rst => {
            let data = rst.json()
            if (data.code === 200) {
                this.setState({
                    userInfo: data.result
                });
                this.props.history.push("/user/:" + this.state.userInfo.user_id)
            }
        }).catch(err => {
            console.log(err)
        })
    }

    render() {
        return (
            <Router>
                <div>
                    <Route path="/user/:{ this.state.userInfo.user_id}" userinfo={this.state.userInfo} component={Profile}/>
                </div>
            </Router>
        );
    }
}

export default User;
